-- author: game developer
-- desc:   short description
-- script: lua

t=0
x=4*8
y=4*8

function TIC()
    -- Move
    if btn(0) then y=y-1 end
    if btn(1) then y=y+1 end
    if btn(2) then x=x-1 end
    if btn(3) then x=x+1 end

    -- Draw
    cls(13)
    map()
    spr(256,x,y,14,1,0,0,1,1)
    spr(256,x,16*8-y,14,1,0,0,1,1)
    t=t+1
end

